import React, { useState } from "react";
import axios from "axios";

const DeletePolicy = ({ changeView }) => {
    const [policyId, setPolicyId] = useState("");

    const handleDelete = () => {
        axios
            .delete(`http://localhost:8080/api/policies/${policyId}`)
            .then(() => alert("Policy deleted successfully!"))
            .catch((error) => console.error("Error deleting policy:", error));
    };

    return (
        <div>
            <h2>Delete Policy</h2>
            <input
                type="text"
                placeholder="Enter Policy ID"
                value={policyId}
                onChange={(e) => setPolicyId(e.target.value)}
            />
            <button onClick={handleDelete}>Delete</button>
            <button onClick={() => changeView("main")}>Back to Main</button>
        </div>
    );
};

export default DeletePolicy;
