import React, { useEffect, useState } from "react";
import axios from "axios";

const ViewPolicies = ({ changeView }) => {
    const [policies, setPolicies] = useState([]);

    useEffect(() => {
        axios
            .get("http://localhost:8080/api/policies")
            .then((response) => setPolicies(response.data))
            .catch((error) => console.error("Error fetching policies:", error));
    }, []);

    return (
        <div>
            <h2>View Policies</h2>
            <table border="1" style={{ margin: "auto", width: "80%", textAlign: "center" }}>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Policy Name</th>
                        <th>Policy Holder Name</th>
                        <th>Start Date</th>
                        <th>End Date</th>
                        <th>Premium</th>
                    </tr>
                </thead>
                <tbody>
                    {policies.map((policy) => (
                        <tr key={policy.id}>
                            <td>{policy.id}</td>
                            <td>{policy.policyName}</td>
                            <td>{policy.policyHolderName}</td>
                            <td>{policy.startDate}</td>
                            <td>{policy.endDate}</td>
                            <td>{policy.premiumAmount}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={() => changeView("main")}>Back to Main</button>
        </div>
    );
};

export default ViewPolicies;
