import React, { useState, useEffect } from "react";
import axios from "axios";

const UpdatePolicy = ({ changeView, id }) => {
    const [policy, setPolicy] = useState({});

    useEffect(() => {
        if (id) {
            axios
                .get(`http://localhost:8080/api/policies/${id}`)
                .then((response) => setPolicy(response.data))
                .catch((error) => console.error("Error fetching policy:", error));
        }
    }, [id]);

    const handleChange = (e) => {
        setPolicy({ ...policy, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios
            .put(`http://localhost:8080/api/policies/${id}`, policy)
            .then(() => alert("Policy updated successfully!"))
            .catch((error) => console.error("Error updating policy:", error));
    };

    return (
        <div>
            <h2>Update Policy</h2>
            <form onSubmit={handleSubmit}>
                <input name="policyName" value={policy.policyName || ""} onChange={handleChange} required />
                <input name="policyHolderName" value={policy.policyHolderName || ""} onChange={handleChange} required />
                <input name="startDate" type="date" value={policy.startDate || ""} onChange={handleChange} required />
                <input name="endDate" type="date" value={policy.endDate || ""} onChange={handleChange} required />
                <input name="premiumAmount" value={policy.premiumAmount || ""} onChange={handleChange} required />
                <button type="submit">Submit</button>
                <button type="button" onClick={() => changeView("main")}>
                    Back to Main
                </button>
            </form>
        </div>
    );
};

export default UpdatePolicy;
