import React, { useState } from "react";
import axios from "axios";

const AddPolicy = () => {
    const [policy, setPolicy] = useState({
        policyName: "",
        policyHolderName: "",
        startDate: "",
        endDate: "",
        premiumAmount: ""
    });

    const handleChange = (e) => {
        setPolicy({ ...policy, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post("http://localhost:8080/api/policies", policy)
            .then(() => {
                alert("Policy added successfully!");
                window.location = "/";
            })
            .catch((error) => {
                console.error("Error adding policy:", error);
            });
    };
    

    return (
        <form onSubmit={handleSubmit}>
            <h1>Add Policy</h1>
            <input name="policyName" placeholder="Policy Name" onChange={handleChange} />
            <input name="policyHolderName" placeholder="Policy Holder Name" onChange={handleChange} />
            <input name="startDate" type="date" onChange={handleChange} />
            <input name="endDate" type="date" onChange={handleChange} />
            <input name="premiumAmount" placeholder="Premium Amount" onChange={handleChange} />
            <button type="submit">Submit</button>
        </form>
    );
};

export default AddPolicy;
