import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const UpdatePolicy = () => {
    const { id } = useParams();
    const [policy, setPolicy] = useState({});

    useEffect(() => {
        axios.get(`http://localhost:8080/api/policies/${id}`)
            .then((response) => setPolicy(response.data))
            .catch((error) => console.error(error));
    }, [id]);

    const handleChange = (e) => {
        setPolicy({ ...policy, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.put(`http://localhost:8080/api/policies/${id}`, policy)
            .then(() => window.location = "/")
            .catch((error) => console.error(error));
    };

    return (
        <form onSubmit={handleSubmit}>
            <h1>Update Policy</h1>
            <input name="policyName" value={policy.policyName || ""} onChange={handleChange} />
            <input name="policyHolderName" value={policy.policyHolderName || ""} onChange={handleChange} />
            <input name="startDate" type="date" value={policy.startDate || ""} onChange={handleChange} />
            <input name="endDate" type="date" value={policy.endDate || ""} onChange={handleChange} />
            <input name="premiumAmount" value={policy.premiumAmount || ""} onChange={handleChange} />
            <button type="submit">Submit</button>
        </form>
    );
};

export default UpdatePolicy;
