import React, { useState } from "react";
import axios from "axios";

const AddPolicy = ({ changeView }) => {
    const [policy, setPolicy] = useState({
        policyName: "",
        policyHolderName: "",
        startDate: "",
        endDate: "",
        premiumAmount: "",
    });

    const handleChange = (e) => {
        setPolicy({ ...policy, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios
            .post("http://localhost:8080/api/policies", policy)
            .then(() => alert("Policy added successfully!"))
            .catch((error) => console.error("Error adding policy:", error));
    };

    return (
        <div style={styles.formContainer}>
            <h2>Add Policy</h2>
            <form onSubmit={handleSubmit}>
                PolicyName:<input name="policyName" placeholder="Policy Name" onChange={handleChange} required /><br></br>
                <input name="policyHolderName" placeholder="Policy Holder Name" onChange={handleChange} required /><br></br>
                <input name="startDate" type="date" onChange={handleChange} required /><br></br>
                <input name="endDate" type="date" onChange={handleChange} required /><br></br>
                <input name="premiumAmount" placeholder="Premium Amount" onChange={handleChange} required /><br></br>
                <button type="submit" style={styles.button}>Submit</button>
                <button type="button" style={styles.button} onClick={() => changeView("main")}>
                    Back to Main
                </button>
            </form>
        </div>
    );
};

const styles = {
    formContainer: {
        textAlign: "center",
        padding: "20px",
        border: "1px solid #ccc",
        borderRadius: "10px",
        width: "80%",
        margin: "auto",
        color:"black",
        backgroundColor: "#f9f9f9",
    },
    button: {
        margin: "10px",
        padding: "10px 20px",
        fontSize: "16px",
        border: "none",
        borderRadius: "5px",
        cursor: "pointer",
        backgroundColor: "#4CAF50",
        color: "black",
    },
};

export default AddPolicy;
