import React, { useState } from "react";
// import Header from "./Header";
import axios from "axios";

const AddPolicy = ({ setCurrentPage, policies, setPolicies }) => {
  const [policy, setPolicy] = useState({
    policyNumber: "",
    policyType: "",
    policyTerm: { startDate: "", endDate: "" },
    premiumAmount: "",
    paymentFrequency: "",
    coverage: { sumAssured: "", nomineeName: "", inclusions: "", exclusions: "" },
    additionalDetails: { claimStatus: "", lastPaymentDate: "", nextPaymentDueDate: "" },
    personalInfo: { fullName: "", dateOfBirth: "", contactNumber: "", emailAddress: "" },
  });
  const [message, setMessage] = useState("");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const keys = name.split(".");
    if (keys.length > 1) {
      setPolicy((prev) => ({
        ...prev,
        [keys[0]]: { ...prev[keys[0]], [keys[1]]: value },
      }));
    } else {
      setPolicy((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleAddPolicy = async (e) => {
    e.preventDefault();
    if (policies.some((p) => p.policyNumber === policy.policyNumber)) {
      setMessage("Policy number must be unique!");
      return;
    }
    try {
      // Sending the data to the backend
      await axios.post('http://localhost:8080/api/policies/add', policy);
      setPolicies([...policies, policy]);
      setMessage("Policy added successfully!");
      setPolicy({
        policyNumber: "",
        policyType: "",
        policyTerm: { startDate: "", endDate: "" },
        premiumAmount: "",
        paymentFrequency: "",
        coverage: { sumAssured: "", nomineeName: "", inclusions: "", exclusions: "" },
        additionalDetails: { claimStatus: "", lastPaymentDate: "", nextPaymentDueDate: "" },
        personalInfo: { fullName: "", dateOfBirth: "", contactNumber: "", emailAddress: "" },
      });
    } catch (error) {
      console.error("Error adding policy:", error);
      setMessage("Failed to add policy");
    }
  };

  return (
    <div style={styles.container}>
      <Header setCurrentPage={setCurrentPage} />
      <h2 style={styles.heading}>Add Policy</h2>
      <form onSubmit={handleAddPolicy} style={styles.form}>
        <div style={styles.grid}>
          {/* Left Column: Personal Information */}
          <div style={styles.leftColumn}>
            <h3 style={styles.subHeading}>Personal Information</h3>
            <div style={styles.inputGroup}>
              <input
                style={styles.input}
                type="text"
                name="personalInfo.fullName"
                placeholder="Full Name"
                value={policy.personalInfo.fullName}
                onChange={handleInputChange}
                required
              />
              <input
                style={styles.input}
                type="date"
                name="personalInfo.dateOfBirth"
                value={policy.personalInfo.dateOfBirth}
                onChange={handleInputChange}
                required
              />
              <input
                style={styles.input}
                type="text"
                name="personalInfo.contactNumber"
                placeholder="Contact Number"
                value={policy.personalInfo.contactNumber}
                onChange={handleInputChange}
                required
              />
              <input
                style={styles.input}
                type="email"
                name="personalInfo.emailAddress"
                placeholder="Email Address"
                value={policy.personalInfo.emailAddress}
                onChange={handleInputChange}
                required
              />
            </div>
          </div>

          {/* Middle Column: Policy Details and Coverage */}
          <div style={styles.middleColumn}>
            <div style={styles.middleRow}>
              <div style={styles.middleColumnLeft}>
                <h3 style={styles.subHeading}>Policy Details</h3>
                <div style={styles.inputGroup}>
                  <input
                    style={styles.input}
                    type="text"
                    name="policyNumber"
                    placeholder="Policy Number"
                    value={policy.policyNumber}
                    onChange={handleInputChange}
                    required
                  />
                  <input
                    style={styles.input}
                    type="text"
                    name="policyType"
                    placeholder="Policy Type"
                    value={policy.policyType}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div style={styles.dateGroup}>
                  <label style={styles.dateLabel}>Start Date</label>
                  <input
                    style={styles.input}
                    type="date"
                    name="policyTerm.startDate"
                    value={policy.policyTerm.startDate}
                    onChange={handleInputChange}
                    required
                  />
                  <label style={styles.dateLabel}>End Date</label>
                  <input
                    style={styles.input}
                    type="date"
                    name="policyTerm.endDate"
                    value={policy.policyTerm.endDate}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div style={styles.inputGroup}>
                  <input
                    style={styles.input}
                    type="number"
                    name="premiumAmount"
                    placeholder="Premium Amount"
                    value={policy.premiumAmount}
                    onChange={handleInputChange}
                    required
                  />
                  <select
                    style={styles.input}
                    name="paymentFrequency"
                    value={policy.paymentFrequency}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Select Frequency</option>
                    <option value="Monthly">Monthly</option>
                    <option value="Annually">Annually</option>
                  </select>
                </div>
              </div>

              <div style={styles.middleColumnRight}>
                <h3 style={styles.subHeading}>Coverage Details</h3>
                <div style={styles.inputGroup}>
                  <input
                    style={styles.input}
                    type="text"
                    name="coverage.nomineeName"
                    placeholder="Nominee Name"
                    value={policy.coverage.nomineeName}
                    onChange={handleInputChange}
                  />
                </div>
                <div style={styles.textareaGroup}>
                  <textarea
                    style={styles.textarea}
                    name="coverage.inclusions"
                    placeholder="Inclusions"
                    value={policy.coverage.inclusions}
                    onChange={handleInputChange}
                    required
                  />
                  <textarea
                    style={styles.textarea}
                    name="coverage.exclusions"
                    placeholder="Exclusions"
                    value={policy.coverage.exclusions}
                    onChange={handleInputChange}
                    required
                  />
                </div>
              </div>
            </div>
          </div>

          {/* Right Column: Additional Details */}
          <div style={styles.rightColumn}>
            <h3 style={styles.subHeading}>Additional Details</h3>
            <div style={styles.inputGroup}>
              <select
                style={styles.input}
                name="additionalDetails.claimStatus"
                value={policy.additionalDetails.claimStatus}
                onChange={handleInputChange}
              >
                <option value="">Select Claim Status</option>
                <option value="Pending">Pending</option>
                <option value="Approved">Approved</option>
                <option value="Rejected">Rejected</option>
              </select>
              <input
                style={styles.input}
                type="date"
                name="additionalDetails.lastPaymentDate"
                value={policy.additionalDetails.lastPaymentDate}
                onChange={handleInputChange}
              />
              <input
                style={styles.input}
                type="date"
                name="additionalDetails.nextPaymentDueDate"
                value={policy.additionalDetails.nextPaymentDueDate}
                onChange={handleInputChange}
              />
            </div>
          </div>
        </div>

        <button type="submit" style={styles.submitButton}>
          Add Policy
        </button>
      </form>
      {message && <p style={styles.message}>{message}</p>}
    </div>
  );
};

const styles = {
  container: {
    margin: "0 auto",
    padding: "20px",
    maxWidth: "1200px",
    fontFamily: "Arial, sans-serif",
    color: "white",
  },
  heading: {
    textAlign: "center",
    marginBottom: "20px",
    color: "white",
