    flexDirection: "column",
    gap: "20px",
  },
  middleRow: {
    display: "flex",
    justifyContent: "space-between",
  },
  middleColumnLeft: {
    flex: 1,
  },
  middleColumnRight: {
    flex: 1,
  },
  subHeading: {
    color: "white",
  },
  inputGroup: {
    display: "flex",
    flexDirection: "column",
    gap: "10px",
  },
  dateGroup: {
    display: "flex",
    gap: "10px",
  },
  dateLabel: {
    color: "white",
  },
  input: {
    padding: "10px",
    borderRadius: "4px",
    border: "1px solid #ccc",
    fontSize: "16px",
  },
  textarea: {
    padding: "10px",
    borderRadius: "4px",
    border: "1px solid #ccc",
    fontSize: "16px",
    height: "100px",
  },
  submitButton: {
    padding: "10px 20px",
    backgroundColor: "#4CAF50",
    color: "white",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
    marginTop: "20px",
  },
  message: {
    textAlign: "center",
    marginTop: "20px",
    color: "#FF6347",
  },
};

export default AddPolicy;
