import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const PolicyList = () => {
    const [policies, setPolicies] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:8080/api/policies")
            .then((response) => setPolicies(response.data))
            .catch((error) => console.error(error));
    }, []);

    const deletePolicy = (id) => {
        axios.delete(`http://localhost:8080/api/policies/${id}`)
            .then(() => setPolicies(policies.filter(policy => policy.id !== id)))
            .catch((error) => console.error(error));
    };

    return (
        <div>
            <h1>Policy List</h1>
            <Link to="/add">Add Policy</Link>
            <ul>
                {policies.map((policy) => (
                    <li key={policy.id}>
                        {policy.policyName} ({policy.policyHolderName}) - 
                        <Link to={`/update/${policy.id}`}>Edit</Link>
                        <button onClick={() => deletePolicy(policy.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default PolicyList;
