import React, { useEffect, useState } from "react";
import axios from "axios";

const ViewPolicies = ({ changeView }) => {
    const [policies, setPolicies] = useState([]);

    useEffect(() => {
        axios
            .get("http://localhost:8080/api/policies")
            .then((response) => setPolicies(response.data))
            .catch((error) => console.error("Error fetching policies:", error));
    }, []);

    return (
        <div>
            <h2>View Policies</h2>
            {policies.map((policy) => (
                <div key={policy.id} style={styles.policyContainer}>
                    {/* Personal Details Section */}
                    <div style={styles.section}>
                        <h3>Personal Details</h3>
                        <div style={styles.inputGroup}>
                            <label>Full Name:</label>
                            <span>{policy.fullName}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>Date of Birth:</label>
                            <span>{policy.dateOfBirth}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>Contact Number:</label>
                            <span>{policy.contactNumber}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>Email Address:</label>
                            <span>{policy.emailAddress}</span>
                        </div>
                    </div>

                    {/* Policy Details Section */}
                    <div style={styles.section}>
                        <h3>Policy Details</h3>
                        <div style={styles.inputGroup}>
                            <label>Policy Name:</label>
                            <span>{policy.policyName}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>Policy Number:</label>
                            <span>{policy.policyNumber}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>Policy Type:</label>
                            <span>{policy.policyType}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>Policy Term (in years):</label>
                            <span>{policy.policyTerm}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>Start Date:</label>
                            <span>{policy.startDate}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>End Date:</label>
                            <span>{policy.endDate}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>Premium Amount:</label>
                            <span>{policy.premiumAmount}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>Payment Frequency:</label>
                            <span>{policy.paymentFrequency}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>Nominee Name:</label>
                            <span>{policy.nomineeName}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>Last Payment Date:</label>
                            <span>{policy.lastPaymentDate}</span>
                        </div>
                        <div style={styles.inputGroup}>
                            <label>Next Payment Due Date:</label>
                            <span>{policy.nextPaymentDueDate}</span>
                        </div>
                    </div>
                </div>
            ))}
            <button onClick={() => changeView("main")}>Back to Main</button>
        </div>
    );
};

const styles = {
    policyContainer: {
        margin: "20px 0",
        padding: "20px",
        border: "1px solid #ccc",
        borderRadius: "8px",
        backgroundColor: "#f9f9f9",
    },
    section: {
        marginBottom: "20px",
        textAlign: "left",
        padding: "0 20px",
    },
    inputGroup: {
        marginBottom: "15px",
        display: "flex",
        justifyContent: "space-between",
    },
    label: {
        fontWeight: "bold",
        marginRight: "10px",
    },
};

export default ViewPolicies;
